import React, { Component } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

var phoneRegEx = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/;

export default class CreateUser extends Component {

    render(){
        return (
           <Formik 
                initialValues={{
                    full_name:'',
                    email:'',
                    contact_number:''
                }}
                validationSchema = {
                    Yup.object().shape({
                        full_name:Yup.string()
                        .required('Full Name is required'),
                        email:Yup.string()
                        .email('please enter valid email')
                        .required('Email is required'),
                        contact_number:Yup.string()
                        .matches(phoneRegEx,'contact number is not valid')
                        .required('contact number is required')

                    })}
                    onSubmit={fields => {
                        e.preventDefault();
       if(this.state.error.full_name === '' && this.state.error.contact === '' && this.state.error.gender === '')
       {const newUser = {
            full_name: this.state.full_name,
            email: this.state.email,
            contact_number: this.state.contact_number,
            gender: this.state.gender,
        };

        axios.post("http://localhost:4000/add", newUser)
            .then(res => {console.log(res.data);});

        this.setState({
            full_name: '',
            email: '',
            contact_number: '',
            gender: '',
        })
         this.props.history.push('/');}
                    }}

                    render={({errors,status,touched}) => (
                        <Form>

                            <div className="form-group">
                                <label htmlFor="full_name">Full Name</label>
                                <Field name="full_name" type="text" className={'form-control' + (errors.full_name && touched.full_name ? ' is-invalid' : '')} />
                                <ErrorMessage name="full_name" component="div" className="invalid-feedback" />
                            </div>
                            <div className="form-group">
                                <label htmlFor="email">Email</label>
                                <Field name="email" type="text" className={'form-control' + (errors.email && touched.email ? ' is-invalid' : '')} />
                                <ErrorMessage name="email" component="div" className="invalid-feedback" />
                            </div>
                            <div className="form-group">
                                <label htmlFor="email">Contact Number</label>
                                <Field name="contact_number" type="text" className={'form-control' + (errors.contact_number && touched.contact_number ? ' is-invalid' : '')} />
                                <ErrorMessage name="contact_number" component="div" className="invalid-feedback" />
                            </div>
                        <div className="form-group">
                            <button type="submit" className="btn btn-primary mr-2">Register</button>
                            <button type="reset" className="btn btn-secondary">Reset</button>
                        </div>
                        </Form>
                    )}
                />
        )
    }

}
