import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


class Todo extends Component {
    constructor(props){
        super(props);
        this.deleteList = this.deleteList.bind(this);
    }
    deleteList(){
        console.log('csdcds'+this.props.todo._id);
        axios.get('http://localhost:4000/delete/'+this.props.todo._id)
            .then(response => {
                console.log('deleted');
            })
            .catch(function (error){
                console.log(error);
            })
    }
    render() {
      return (
        <tr>
        <td>{this.props.todo.todo_description}</td>
        <td>{this.props.todo.todo_responsible}</td>
        <td>{this.props.todo.todo_priority}</td>
        <td>
            <Link to={"/edit/"+this.props.todo._id}>Edit</Link>
        </td>
        <td>
            <button onClick={this.deleteList} className="btn"><span className="badge badge-primary">
                Delete
            </span>
            </button>
        </td>

    </tr>
      );
    }
  }

export default class TodosList extends Component {

    constructor(props) {
        super(props);
        this.state = {todos: []};
    }



    todoList() {
        axios.get('http://localhost:4000')
        .then(response => {
            this.setState({ todos: response.data });
        })
        .catch(function (error){
            console.log(error);
        })
        console.log('vfdvdfvdfvdf')
        return this.state.todos.map(function(currentTodo, i){
            return <Todo todo={currentTodo} key={i} />;
        })
    }

    render() {
        return (
            <div>
                <h3>Todos List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Responsible</th>
                            <th>Priority</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.todoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
